mapper.select:
SELECT p.no, p.title, p.content, p.writer, p.regdate, p.attachment, u.v_id, u.v_name
FROM userinfo u, port p
WHERE p.writer = u.v_id AND no = #{no}

mapper.update:
UPDATE port 
SET title = #{title}, content = #{content}, regdate = SYSDATE, attachment = #{attachment}
WHERE no = #{no}

UPDATE port 
SET views = #{views}
WHERE no = #{no}
