mapper.insert:

	SELECT sale_dat_seq.NEXTVAL no FROM DUAL

INSERT INTO sale_dat (dat_no, board_no, writer, content, regdate)
VALUES (#{dat_no}, #{board_no}, #{writer}, #{content}, SYSDATE);

mapper.select:
SELECT p.dat_no, p.board_no, p.writer, p.content, p.regdate u.v_id, u.v_name
FROM userinfo u, sale_dat p
WHERE p.writer = u.v_id AND no = #{no}

mapper.update:
UPDATE sale_dat
SET content = #{content}, regdate = SYSDATE
WHERE no = #{no}
