mapper.select:
SELECT o.or_no, o.v_id as "UID", o.sale_no ,s.title, s.attachment, s.price, date_format(regdate,"%Y %c/%e") as "REGDATE", o.state, u.v_email, u.v_name
FROM userinfo u, order_list o, sale s
WHERE o.v_id = u.v_id AND o.sale_no = s.no AND o.or_no = #{or_no};

mapper.update:
UPDATE order_list 
SET state = #{state}
WHERE or_no = #{or_no}
